  "customEval": [
    {
      "test": "[...game.user.targets].length &lt; 1 || [...game.user.targets].length &gt; 9 || game.actors.filter(a1 =&gt; [...game.user.targets].map(t =&gt; t.data.actorId).includes(a1.id)).filter(a =&gt; a.data.effects.filter(e =&gt; e.data.label === 'Brulure' || e.data.label === 'Effet de Terre').length).length",
