{
  "action": "-9",
  "mana": "-1",
  "nbTargets": "6+@dex",
  "damage": "(3+@for+1d6)[force]",
  "zeal": "+2",
  "minReach": "1",
  "maxReach": "6+@dex",
  "customEval": [
    {
      "test": "!game.user.targets.size || ([...game.user.targets].filter(t =>((t.x === game.canvas?.scene?.tokens?.find(t => t.actor?.id === game.user?.character?.id).data.x) && (t.x === [...game.user.targets][0].x) && (((t.y > game.canvas?.scene?.tokens?.find(t => t.actor?.id === game.user?.character?.id).data.y) && (game.canvas?.scene?.tokens?.find(t => t.actor?.id === game.user?.character?.id).data.y < [...game.user.targets][0].y)) || ((t.y < game.canvas?.scene?.tokens?.find(t => t.actor?.id === game.user?.character?.id).data.y) && (game.canvas?.scene?.tokens?.find(t => t.actor?.id === game.user?.character?.id).data.y > [...game.user.targets][0].y)))) || ((t.y === game.canvas?.scene?.tokens?.find(t => t.actor?.id === game.user?.character?.id).data.y) && (t.y === [...game.user.targets][0].y) && (((t.x > game.canvas?.scene?.tokens?.find(t => t.actor?.id === game.user?.character?.id).data.x) && (game.canvas?.scene?.tokens?.find(t => t.actor?.id === game.user?.character?.id).data.x < [...game.user.targets][0].x)) || ((t.x < game.canvas?.scene?.tokens?.find(t => t.actor?.id === game.user?.character?.id).data.x) && (game.canvas?.scene?.tokens?.find(t => t.actor?.id === game.user?.character?.id).data.x > [...game.user.targets][0].x))))).length === [...game.user.targets].length)",
      "message": "Toutes les cibles doivent être alignés devant vous! "
    }
  ],
  "other": [
    "Déplacez-vous de 7+@dex cases dans la directions des cibles"
  ]
}

