{
  "action": "-10",
  "zeal": "1",
  "addingRolls": [
    {
      "title": "Tireur d'élite",
      "formula": "1",
      "effects": [
        {
          "result": "1",
          "timer": "300",
          "data": [
            {
              "label": "Tireur d'élite",
              "icon": "icons/skills/ranged/person-archery-bow-attack-orange.webp",
              "changes": [
                {
                  "key": "data.attributes.critical",
                  "value": "10+@str",
                  "mode": "2"
                },
                {
                  "key": "macro.tokenMagic",
                  "value": "adjustment",
                  "mode": "0"
                }
              ],
              "duration": {
                "startTime": "null",
                "rounds": "2",
                "turns": "null"
              }
            }
          ],
          "other": "Augmente le critique de @str + 10 jusqu'a la fin du prochain tour"
        }
      ]
    }
  ]
}

{
  "damage": "(@dex+1d8)[piercing]",
  "minReach": "4",
  "maxReach": "12",
  "nbTargets": "5",
  "action": "-8",
  "zeal": "-1",
  "customEval": [
    {
      "test": "game.user.targets.size === 1 || game.user.targets.size &gt; 9 || ([...game.user.targets].map(t =&gt; t.x/game.canvas?.scene?.dimensions?.size ).every((val, index, arr) =&gt; arr.every((v, i) =&gt; i === index || Math.abs(val - v) &lt;= 2)) &amp;&amp; [...game.user.targets].map(t =&gt; t.y/game.canvas?.scene?.dimensions?.size ).every((val, index, arr) =&gt; arr.every((v, i) =&gt; i === index || Math.abs(val - v) &lt;= 2)))",
      "message": "Toutes les cibles doivent être contenues dans un carré 3X3 cases"
    }
  ]
}

{
  "damage": "(@dex+1d6)[piercing]",
  "minReach": "3",
  "maxReach": "9",
  "nbTargets": "2",
  "action": "-9",
  "zeal": "+1",
  "mana": "-1",
  "customEval": [
    {
      "test": "game.user.targets.size === 1 || game.user.targets.size !== 2 || ((Math.abs([...game.user.targets][0].x - [...game.user.targets][1].x) ===  game.canvas?.scene?.dimensions?.size &amp;&amp; [...game.user.targets][0].y === [...game.user.targets][1].y ) || (Math.abs([...game.user.targets][0].y - [...game.user.targets][1].y) ===  game.canvas?.scene?.dimensions?.size  &amp;&amp; [...game.user.targets][0].x === [...game.user.targets][1].x))",
      "message": "Les deux cibles doivent être adjacentes"
    }
  ]
}

{
  "zeal": "+1",
  "customEval": [
    {
      "test": "(!!game.combat.data.flags.FQLogs &amp;&amp; (!![...game.combat.data.flags.FQLogs].filter(log =&gt; log.round === game.combat.round &amp;&amp; log.actorId === game.user.character.id).filter(l =&gt; l.resultArray.filter(r =&gt; r.evasion).length).length))",
      "message": "Vous devez avoir joué un sort qui a été esquivé pendant ce tour"
    }
  ],
  "addingRolls": [
    {
      "title": "Bonus de dégâts",
      "formula": "1",
      "effects": [
        {
          "result": "1",
          "timer": "300",
          "data": [
            {
              "label": "Bonus de dégâts +1",
              "icon": "icons/skills/wounds/anatomy-organ-heart-red.webp",
              "changes": [
                {
                  "key": "data.bonuses.All-Damage",
                  "value": "1",
                  "mode": "2"
                },
                {
                  "key": "data.bonuses.other.damage",
                  "value": "1",
                  "mode": "2"
                }
              ],
              "duration": {
                "startTime": "null",
                "rounds": "null",
                "turns": "null"
              }
            }
          ],
          "other": "Augmente les dégâts de 1 jusqu'à la fin du combat"
        }
      ]
    }
  ],
  "other": [
    "Récupérez le(s) carte(s) concernée(s) dans votre défausse, et mettez ce(s) carte(s) dans votre main"
  ]
}

{
  "name": "Embuscade",
  "action": "-XXX",
  "xvalue": "action.value",
  "customEval": [
    {
      "test": "(!game.combat.data.flags.FQLogs || ![...game.combat.data.flags.FQLogs].filter(log =&gt; log.round === game.combat.round &amp;&amp; log.actorId === game.user.character.id)?.pop()) || ([...game.combat.data.flags.FQLogs].filter(log =&gt; log.round === game.combat.round &amp;&amp; log.actorId === game.user.character.id)?.pop().cardContent.name === 'Embuscade')",
      "message": "Impossible de jouer ce sort si une carte a déjà été joué ce tour-ci"
    }
  ],
  "addingRolls": [
    {
      "title": "Bonus de dégâts",
      "formula": "1",
      "effects": [
        {
          "result": "1",
          "timer": "300",
          "data": [
            {
              "label": "Bonus de dégâts +@wis",
              "icon": "icons/skills/targeting/crosshair-scope-sniper-green.webp",
              "changes": [
                {
                  "key": "data.bonuses.All-Damage",
                  "value": "@wis",
                  "mode": "2"
                },
                {
                  "key": "data.bonuses.other.damage",
                  "value": "@wis",
                  "mode": "2"
                }
              ],
              "duration": {
                "startTime": "null",
                "rounds": "2",
                "turns": "null"
              }
            }
          ],
          "other": "Augmente les dégâts de @wis pendant 2 tour"
        }
      ]
    }
  ],
  "other": [
    "vos points d'action sont à 0",
    "Vous DEVEZ passez votre tour maintenant ou jouez une autre carte 'Embuscade'"
  ]
}

{
  "damage": "(@dex+1d6)[force]",
  "minReach": "3",
  "maxReach": "7",
  "nbTargets": "5",
  "action": "-8",
  "mana": "-1",
  "zeal": "1",
  "customEval": [
    {
      "test": "game.user.targets.size === 1 || game.user.targets.size &gt; 5 || ([...game.user.targets].filter(pt =&gt; [...game.user.targets].filter(t =&gt; (Math.abs(t.x - pt.x) === game.canvas?.scene?.dimensions?.size &amp;&amp; t.y === pt.y) || (Math.abs(t.y - pt.y) === game.canvas?.scene?.dimensions?.size &amp;&amp; t.x === pt.x)).length === [...game.user.targets].length - 1).length &gt;= 1)",
      "message": "Toutes les cibles doivent être adjacentes"
    }
  ]
}
